// eslint-disable-next-line no-undef
    $('.clickable').bind('click', function(ev) {
        let $div = $(ev.target);

        let offset = $div.offset();
        let x = ev.clientX - offset.left - 775;
        let y = ev.clientY - offset.top - 725;
        console.log("x:"+x);
        console.log("y:"+y);
        let payload = {"x_value":x,"y_value":y};
        let server = "http://127.0.0.1:5000";
        let appdir='/data';
        $.ajax({
            type: "POST",
            url:server+appdir,
            data: JSON.stringify(payload),
            dataType: 'json',
            crossDomain: true,
    }).done(function(data) {
            console.log("data sent");
        });
    });
    $('#btn').click(function(){
        let payload = {"x_value":0,"y_value":0};
        let server = "http://127.0.0.1:5000";
        let appdir='/data';
        $.ajax({
            type:"POST",
            url:server+appdir,
            data: JSON.stringify(payload),
            dataType: 'json',
            crossDomain: true
        }).done(
            function(data){
                console.log("data sent");
            }
        );
    });

